import React, { Component } from "react"
import "./styles.css"

class App extends Component {

    constructor(props){
        super(props);

        this.state = {
            textoFrase: ''
        };

        this.quebraBiscoito = this.quebraBiscoito.bind(this);

        this.frases = ['Siga os bons e aprenda com eles.', 'O bom-senso vale mais do que muito conhecimento.', 
                        'O riso é a menor distância entre duas pessoas.', 
                        'Deixe de lado as preocupações e seja feliz.',
                        'Realize o óbvio, pense no improvável e conquiste o impossível.',
                        'Acredite em milagres, mas não dependa deles.',
                        'A maior barreira para o sucesso é o medo do fracasso.'];
    }

    quebraBiscoito(){
        const frases = this.frases;
        
        let ramdomNumber = Math.floor(Math.random() * frases.length);

        const fraseEscolhida = `"${frases[ramdomNumber]}"`;
        
        this.setState({textoFrase: fraseEscolhida});
    }

    render(){

        return(
            <div className="container">
                <img src={require('./assets/biscoito.png')} className="img"></img>
                <Button nome="Abrir Bicoito" acaoBtn={this.quebraBiscoito}/>
                <h3 className="textoFrase">{this.state.textoFrase}</h3>
            </div>
        );
    };
}

class Button extends Component{
    render(){
        return(
            <div>
                <button onClick={this.props.acaoBtn}>{this.props.nome}</button>
            </div>
        );
    }
}

export default App
