import React, { Component } from "react"

class App extends Component {

    constructor(props){
        super(props);

        this.state = {
            nome: '',
            email: '',
            senha: '',
            error: '',
            sexo: ''
        };

        this.cadastrar = this.cadastrar.bind(this);
    }

    cadastrar(event){
        const {nome, email, senha} = this.state;

        console.log(nome, email, senha);

        if(nome !== '' && email !== '' && senha !== ''){
            alert(`Nome: ${nome} \nEmail: ${email} \nSenha: ${senha}`);
        } else {

            this.setState({error: 'Nem todos os campos foram preenchidos. Por favor verifique.'});
            console.log(this.state);
        }

        event.preventDefault();
    }

    trocaSexo(e){
        let typedValue = e.target.value

        this.setState({sexo: typedValue});
    }

    render(){

        return(
            <div>
                <h1>Login</h1>
                {this.state.error && <p>{this.state.error}</p>}

                <form onSubmit={this.cadastrar}>
                    <label>E-mail:</label>
                    <input type="email" value={this.email} 
                            onChange={(e) => this.setState({email: e.target.value})}/><br/>

                    <label>Senha:</label>
                    <input type="text" name="senha" value={this.senha} 
                            onChange={(e) => this.setState({senha: e.target.value})}/><br/>

                    <label>Senha:</label>
                    <select name="sexo" value={this.sexo} onChange={this.trocaSexo}>
                        <option value="masculino">Masculino</option>
                        <option value="feminino">Feminino</option>
                    </select><br/>

                    <div>
                        <h3>{this.email}</h3>
                        <h3>{this.senha}</h3>
                        <h3>{this.sexo}</h3>
                    </div>

                    <button type="submit">Cadastrar</button>
                </form>
            </div>
        );
    };
}

export default App
