            nome: '',
            email: '',
            senha: '',
        }
    }

    render(){

        return(
            <div>
                <h1>Novo Usu√°rio</h1>

                <form>
                    <input type="text" value={this.state.nome} 
                            onChange={(e) => this.setState({nome: e.target.value})}/>

                    <input type="email" value={this.email} 
                            onChange={(e) => this.setState({nome: e.target.value})}/>
                </form>
            </div>
        );
    };
}

export default App
