import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props){
    super(props);

    this.state = {
      numero: 0,
      playPause: 'PLAY',
      classBtn: 'botao'
    }

    this.timer = null;
    this.playPause = this.playPause.bind(this);
    this.limpar = this.limpar.bind(this);
  }

  playPause(){
    let state = this.state;

    if(this.timer !== null){
      clearInterval(this.timer);
      this.timer = null;
      state.playPause = "PLAY";
      this.setState(state);
    }else{
        this.timer = setInterval(() => {
          let state = this.state;
          state.numero += 0.1;
          this.setState(state);
        }, 100);
        state.playPause = "PAUSE";     
    }
    this.setState(state);
  }

  limpar(){

    let state = this.state;
    if(state.numero !== 0){     
      clearInterval(this.timer);
      this.timer = null;
      state.numero = 0;
      state.playPause = 'PLAY'
      this.setState(state);
    }
  }

  render(){

    return (
      <div className="App">
          <img src={require('./assets/cronometro.png')} className="img" />
          <a className="timer" >{this.state.numero.toFixed(1)}</a>
          <div className="areaBtn">
            <a className="botao" onClick={this.playPause}>{this.state.playPause}</a>
            <a className={this.state.numero === 0 ? 'botaoClicado' : 'botao'} onClick={this.limpar}>LIMPAR</a>
          </div>
      </div>
    );
  }
}

export default App;
