import React, { Component } from "react"

class App extends Component {

    constructor(props){
        super(props);

        this.state = {
            form: {
                nome: 'teste',
                email: 'teste',
                senha: 'teste',
                error: 'teste',
                sexo: 'teste'
            }
        };

        this.cadastrar = this.cadastrar.bind(this);
        this.dadosForm = this.dadosForm.bind(this);
    }

    cadastrar(event){
        const {nome, email, senha} = this.state;

        console.log(nome, email, senha);

        if(nome !== '' && email !== '' && senha !== ''){
            alert(`Nome: ${nome} \nEmail: ${email} \nSenha: ${senha}`);
        } else {

            this.setState({error: 'Nem todos os campos foram preenchidos. Por favor verifique.'});
            console.log(this.state);
        }

        event.preventDefault();
    }

    dadosForm(e){
        let form = this.state.form;

        form[e.target.name] = e.target.value;

        this.setState({form: form});
    }

    render(){

        return(
            <div>
                <h1>Login</h1>
                {this.state.error && <p>{this.state.error}</p>}

                <form onSubmit={this.cadastrar}>
                    <label>E-mail:</label>
                        <input type="text" name="nome" value={this.state.form.nome} 
                                onChange={(e) => this.dadosForm}/><br/>

                    <label>E-mail:</label>
                    <input type="email" name="name" value={this.state.form.email} 
                            onChange={(e) => this.dadosForm}/><br/>

                    <label>Senha:</label>
                    <input type="text" name="senha" value={this.state.form.senha} 
                            onChange={(e) => this.dadosForm}/><br/>

                    <label>Senha:</label>
                    <select name="sexo" value={this.state.form.sexo} onChange={(e) => this.dadosForm}>
                        <option value="masculino">Masculino</option>
                        <option value="feminino">Feminino</option>
                    </select><br/>

                    <div>
                        <h3>{this.state.form.email}</h3>
                        <h3>{this.state.form.senha}</h3>
                        <h3>{this.state.form.sexo}</h3>
                    </div>

                    <button type="submit">Cadastrar</button>
                </form>
            </div>
        );
    };
}

export default App
