import React, { Component } from "react"

class ToDoList extends Component {
  
    constructor(props){
      super(props);
  
      this.state = {
          tarefa: '',
          items: []
      }

      this.addItem = this.addItem.bind(this);
      this.log = this.log.bind(this);
    }

    log(){
        console.log(this.state.items);
    }

    addItem(e){
        let state = this.state;
        if(this._tarefaInput.value !== ''){
            console.log(this._tarefaInput.value);
            let newItem = {
                text: this._tarefaInput.value,
                key: Date.now()
            };

            this.setState({...state.items, newItem});
        }

        e.preventDefault();
    }
  
    render(){
  
      return (
        <div className="ToDoList">
            <form onSubmit={this.addItem(e)}>
                <input type="text" placeholder="Nova Tarefa" name="tarefa"
                        value={this.state.tarefa} onChange={(e) => this.setState({tarefa: e.target.value})} 
                        ref={(e) => this._tarefaInput = e} />
                <button type="submit">
                    Adicionar
                </button>
            </form>

            <button onClick={this.log}>LOG</button>
        </div>
      );
    }
  }
  
  export default ToDoList;
